curl -s -X "POST" "http://localhost:8088/ksql" -H "Content-Type: application/vnd.ksql.v1+json; charset=utf-8" -d '{"ksql": "show queries;", "streamsProperties": {}}' 2>/dev/null | jq -r '.[].queries[].id' | xargs -t -I {} curl -s -X "POST" "http://localhost:8088/ksql" -H "Content-Type: application/vnd.ksql.v1+json; charset=utf-8" -d'{"ksql": "explain {};"}' | jq '.[0].queryDescription | {id:.id, state:.state}'